create table if not exists voltage
(
    id             bigint PRIMARY KEY GENERATED ALWAYS AS IDENTITY UNIQUE,
    value bigint not null
);
create table if not exists attenuation_frequency(
    id bigint PRIMARY KEY GENERATED always as identity unique ,
    value bigint not null
);
create table if not exists lead_equivalent(
    id  bigint primary key generated always as identity unique ,
    thickness numeric (5,2) not null ,
    voltage_id bigint not null ,
    attenuation_frequency_id bigint not null ,
    constraint fk_voltage_id FOREIGN KEY (voltage_id) references voltage(id),
    constraint fk_attenuation_frequency_id FOREIGN KEY (attenuation_frequency_id) references attenuation_frequency(id)
);

